interface MobileBottomNavProps {
  cartCount: number;
  activeTab?: string;
}

export function MobileBottomNav({ cartCount, activeTab = "home" }: MobileBottomNavProps) {
  const tabs = [
    { id: "home", icon: Home, label: "Home" },
    { id: "categories", icon: Grid3x3, label: "Categories" },
    { id: "cart", icon: ShoppingCart, label: "Cart", badge: cartCount },
    { id: "account", icon: User, label: "Account" },
  ];

  return (
    <div className="md:hidden fixed bottom-0 left-0 right-0 z-40 bg-background border-t shadow-lg">
      <div className="grid grid-cols-4 h-16">
        {tabs.map((tab) => {
          const isActive = activeTab === tab.id;
          const Icon = tab.icon;

          return (
            <button
              key={tab.id}
              className={`flex flex-col items-center justify-center gap-1 relative transition-colors ${
                isActive
