import { X, ShoppingBag, Trash2, Package } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { CartItemWithProduct } from "@shared/schema";

interface MiniCartProps {
  isOpen: boolean;
  onClose: () => void;
  items: CartItemWithProduct[];
  onUpdateQuantity: (itemId: string, quantity: number) => void;
  onRemoveItem: (itemId: string) => void;
  onCheckout: () => void;
}

export function MiniCart({
  isOpen,
  onClose,
  items,
  onUpdateQuantity,
  onRemoveItem,
  onCheckout,
}: MiniCartProps) {
  const subtotal = items.reduce(
    (sum, item) => sum + Number(item.product.price) * item.quantity,
    0
  );

  const freeShippingThreshold = 999;
  const remainingForFreeShipping = Math.max(0, freeShippingThreshold - subtotal);
  const shippingProgress = Math.min(100, (subtotal / freeShippingThreshold) * 100);

  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/20 z-40"
        onClick={onClose}
        data-testid="cart-backdrop"
      />

      {/* Cart Panel */}
      <div
        className="fixed top-0 right-0 h-full w-full sm:w-96 bg-background border-l shadow-2xl z-50 flex flex-col"
        data-testid="mini-cart"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b">
          <div className="flex items-center gap-2">
            <ShoppingBag className="w-5 h-5" />
            <h2 className="text-lg font-bold">Shopping Cart</h2>
            <Badge>{items.length}</Badge>
          </div>
          <Button
            size="icon"
            variant="ghost"
            onClick={onClose}
            data-testid="button-close-cart"
          >
            <X className="w-5 h-5" />
          </Button>
        </div>

        {/* Free Shipping Progress */}
        {remainingForFreeShipping > 0 && (
          <div className="p-4 bg-muted/50 border-b">
            <p className="text-sm text-muted-foreground mb-2">
              Add <span className="font-semibold text-foreground">${remainingForFreeShipping}</span> more for FREE shipping!
            </p>
            <div className="h-2 bg-muted rounded-full overflow-hidden">
              <div
                className="h-full bg-primary transition-all duration-300"
                style={{ width: `${shippingProgress}%` }}
              />
            </div>
          </div>
        )}

        {/* Cart Items */}
        <div className="flex-1 overflow-y-auto">
          {items.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full p-8 text-center">
              <ShoppingBag className="w-16 h-16 text-muted-foreground/50 mb-4" />
              <p className="text-lg font-semibold mb-2">Your cart is empty</p>
              <p className="text-sm text-muted-foreground mb-6">
                Add some products to get started
              </p>
              <Button onClick={onClose} data-testid="button-continue-shopping">
                Continue Shopping
              </Button>
            </div>
          ) : (
            <div className="divide-y">
              {items.map((item) => (
                <div
                  key={item.id}
                  className="flex gap-3 p-4"
                  data-testid={`cart-item-${item.id}`}
                >
                  {/* Product Image */}
                  <div className="w-16 h-16 bg-muted rounded flex items-center justify-center flex-shrink-0">
                    <Package className="w-8 h-8 text-muted-foreground" />
                  </div>

                  {/* Product Details */}
                  <div className="flex-1 min-w-0">
                    <h3 className="font-semibold text-sm line-clamp-2 mb-1">
                      {item.product.name}
                    </h3>
                    <p className="text-sm font-bold text-foreground mb-2">
                      ${Number(item.product.price).toLocaleString()}
                    </p>

                    {/* Quantity Controls */}
                    <div className="flex items-center gap-2">
                      <div className="flex items-center border rounded">
                        <button
                          onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}
                          className="px-2 py-1 hover:bg-muted disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                          disabled={item.quantity <= 1}
                          data-testid={`button-decrease-quantity-${item.id}`}
                        >
                          -
                        </button>
                        <span className="px-3 py-1 text-sm font-medium" data-testid={`quantity-${item.id}`}>
                          {item.quantity}
                        </span>
                        <button
                          onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}
                          className="px-2 py-1 hover:bg-muted transition-colors"
                          data-testid={`button-increase-quantity-${item.id}`}
                        >
                          +
                        </button>
                      </div>

                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => onRemoveItem(item.id)}
                        className="h-8 w-8"
                        data-testid={`button-remove-item-${item.id}`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Footer */}
        {items.length > 0 && (
          <div className="border-t p-4 space-y-4">
            <div className="flex items-center justify-between text-lg font-bold">
              <span>Subtotal:</span>
              <span data-testid="cart-subtotal">${subtotal.toLocaleString()}</span>
            </div>

            <Button
              className="w-full h-12 text-base font-semibold"
              onClick={onCheckout}
              data-testid="button-checkout"
            >
              Proceed to Checkout
            </Button>

            <p className="text-xs text-center text-muted-foreground">
              Shipping and taxes calculated at checkout
            </p>
          </div>
        )}
      </div>
    </>
  );
}

client/src/components/SocialProof.tsx.content:
import { Star, Quote, CheckCircle } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import type { Review } from "@shared/schema";

interface SocialProofProps {
  reviews: Review[];
}

export function SocialProof({ reviews }: SocialProofProps) {
  if (reviews.length === 0) {
    return null;
  }

  const averageRating = reviews.reduce((acc, review) => acc + review.rating, 0) / reviews.length;
  const totalCustomers = "10M+";

  return (
    <section className="py-12 md:py-16 bg-muted/30">
      <div className="max-w-screen-2xl mx-auto px-4 md:px-6">
        {/* Header */}
        <div className="text-center mb-8 md:mb-12">
          <h2 className="text-2xl md:text-3xl lg:text-4xl font-bold mb-2">
            Loved by Customers Across India
          </h2>
          <div className="flex items-center justify-center gap-2 text-muted-foreground">
            <div className="flex items-center gap-1">
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  className={`w-5 h-5 ${
                    i < Math.floor(averageRating)
                      ? "fill-yellow-400 text-yellow-400"
                      : "fill-muted text-muted"
                  }`}
                />
              ))}
            </div>
            <span className="text-lg font-semibold text-foreground">
              {averageRating.toFixed(1)}/5.0
            </span>
            <span>•</span>
            <span>Join {totalCustomers} Happy Customers</span>
          </div>
        </div>

        {/* Trust Badges */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-12">
          {[
            { label: "Verified Purchases", value: "100%", icon: CheckCircle },
            { label: "Customer Satisfaction", value: "4.8/5", icon: Star },
            { label: "On-Time Delivery", value: "98%", icon: CheckCircle },
            { label: "Easy Returns", value: "7 Days", icon: CheckCircle },
          ].map((stat, index) => (
            <div
              key={index}
              className="bg-card border border-card-border rounded-lg p-4 text-center"
              data-testid={`stat-${index}`}
            >
              <div className="flex items-center justify-center w-10 h-10 bg-primary/10 rounded-full mx-auto mb-2">
                <stat.icon className="w-5 h-5 text-primary" />
              </div>
              <div className="text-2xl font-bold text-foreground mb-1">{stat.value}</div>
              <div className="text-sm text-muted-foreground">{stat.label}</div>
            </div>
          ))}
        </div>

        {/* Reviews Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reviews.slice(0, 6).map((review) => (
            <div
              key={review.id}
              className="bg-card border border-card-border rounded-lg p-6 hover-elevate transition-all"
              data-testid={`review-card-${review.id}`}
            >
              {/* Quote Icon */}
              <Quote className="w-8 h-8 text-primary/20 mb-3" />

              {/* Rating */}
              <div className="flex items-center gap-1 mb-3">
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={i}
                    className={`w-4 h-4 ${
                      i < review.rating
                        ? "fill-yellow-400 text-yellow-400"
                        : "fill-muted text-muted"
                    }`}
                  />
                ))}
              </div>

              {/* Comment */}
              <p className="text-foreground mb-4 line-clamp-4">{review.comment}</p>

              {/* Customer Info */}
              <div className="flex items-center gap-3 pt-4 border-t">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-primary/30 to-accent/30 flex items-center justify-center text-xl font-bold">
                  {review.customerName.charAt(0)}
                </div>
                <div className="flex-1">
                  <div className="flex items-center gap-2">
                    <p className="font-semibold text-foreground">{review.customerName}</p>
                    {review.verified && (
                      <Badge variant="secondary" className="text-xs">
                        <CheckCircle className="w-3 h-3 mr-1" />
                        Verified
                      </Badge>
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground">
                    {review.customerLocation} • {review.date}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Media Mentions */}
        <div className="mt-12 pt-12 border-t">
          <p className="text-center text-sm text-muted-foreground mb-6">AS FEATURED IN</p>
          <div className="flex flex-wrap items-center justify-center gap-8 md:gap-12 opacity-50">
            {["Economic Times", "Vogue India", "Femina", "Grazia", "Elle"].map((brand) => (
              <div key={brand} className="text-lg font-bold text-foreground">
                {brand}
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

client/src/components/FlashDeals.tsx.content:
import { useState, useEffect } from "react";
import { Clock, Flame, Eye } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { ProductCard } from "./ProductCard";
import type { DealWithProduct } from "@shared/schema";

interface FlashDealsProps {
  deals: DealWithProduct[];
  onAddToCart: (productId: string) => void;
  onToggleWishlist: (productId: string) => void;
  onQuickView: (productId: string) => void;
  wishlistedProducts?: Set<string>;
}

export function FlashDeals({
  deals,
  onAddToCart,
  onToggleWishlist,
  onQuickView,
  wishlistedProducts = new Set(),
}: FlashDealsProps) {
  const [timeLeft, setTimeLeft] = useState({
    hours: 5,
    minutes: 23,
    seconds: 45,
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        let { hours, minutes, seconds } = prev;

        if (seconds > 0) {
          seconds--;
        } else if (minutes > 0) {
          minutes--;
          seconds = 59;
        } else if (hours > 0) {
          hours--;
          minutes = 59;
          seconds = 59;
        }

        return { hours, minutes, seconds };
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  if (deals.length === 0) {
    return null;
  }

  return (
    <section className="py-12 md:py-16 bg-gradient-to-br from-destructive/5 via-background to-primary/5">
      <div className="max-w-screen-2xl mx-auto px-4 md:px-6">
        {/* Header with Countdown */}
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-8">
          <div className="flex items-center gap-3">
            <div className="flex items-center justify-center w-12 h-12 bg-destructive/10 rounded-full">
              <Flame className="w-6 h-6 text-destructive" />
            </div>
            <div>
              <h2 className="text-2xl md:text-3xl font-bold flex items-center gap-2">
                Flash Deals
                <Badge variant="destructive" className="text-xs">HOT</Badge>
              </h2>
              <p className="text-muted-foreground">Limited time offers - Grab them fast!</p>
            </div>
          </div>

          {/* Countdown Timer */}
          <div className="flex items-center gap-3 bg-card border border-card-border rounded-lg p-4">
            <Clock className="w-5 h-5 text-primary" />
            <div className="flex items-center gap-1">
              <span className="text-sm text-muted-foreground">Ends in:</span>
              <div className="flex items-center gap-1 ml-2">
                <div className="flex flex-col items-center">
                  <span className="text-2xl font-bold text-foreground" data-testid="deal-timer-hours">
                    {String(timeLeft.hours).padStart(2, "0")}
                  </span>
                  <span className="text-xs text-muted-foreground">HRS</span>
                </div>
                <span className="text-xl font-bold text-muted-foreground">:</span>
                <div className="flex flex-col items-center">
                  <span className="text-2xl font-bold text-foreground" data-testid="deal-timer-minutes">
                    {String(timeLeft.minutes).padStart(2, "0")}
                  </span>
                  <span className="text-xs text-muted-foreground">MIN</span>
                </div>
                <span className="text-xl font-bold text-muted-foreground">:</span>
                <div className="flex flex-col items-center">
                  <span className="text-2xl font-bold text-foreground" data-testid="deal-timer-seconds">
                    {String(timeLeft.seconds).padStart(2, "0")}
                  </span>
                  <span className="text-xs text-muted-foreground">SEC</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Deals Grid */}
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4 md:gap-6">
          {deals.map((deal) => (
            <div key={deal.id} className="relative">
              <ProductCard
                product={deal.product}
                onAddToCart={onAddToCart}
                onToggleWishlist={onToggleWishlist}
                onQuickView={onQuickView}
                isWishlisted={wishlistedProducts.has(deal.product.id)}
              />

              {/* View Counter */}
              {deal.viewCount > 0 && (
                <div className="absolute top-2 left-2 bg-background/90 backdrop-blur-sm px-2 py-1 rounded text-xs font-medium flex items-center gap-1 z-10">
                  <Eye className="w-3 h-3" />
                  <span data-testid={`view-count-${deal.id}`}>{deal.viewCount} viewing</span>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Urgency Banner */}
        <div className="mt-8 bg-destructive/10 border border-destructive/20 rounded-lg p-4 text-center">
          <p className="text-sm md:text-base font-semibold text-destructive">
            ⚡ Hurry! Deals selling out fast - Limited stock available
          </p>
        </div>
      </div>
    </section>
  );
}

